from urllib.error import HTTPError, URLError
import urllib.request as req


#저장 경로를 - list 형식으로 처리

urllist = list(["data:image/jpeg;base64,"
                ,"https://www.google.com"])

pathList = list(["C:\\Users\\sdedu\\Desktop\\Python_lib\\crawling\\catpic2.png",
                "C:\\Users\\sdedu\\Desktop\\Python_lib\\crawling\\index.html"])

for i, url in enumerate(urllist):
    #예외처리
    try:
        #web에 대한 수신 정보를 확인
        res = req.urlopen(url)
        
        #응답받은 내용
        content = res.read()
        print("---------------")
        
        #상태 정보 확인
        print(f"헤더 정보 확인 : {i, res.info()}")
        print(f"http status : {res.getcode()}")
        print("------------")
        
        #파일 쓰기
        #wb - 이진 쓰기 모드 (binary)
        #with 사용 - 파일을 열고, 닫는 기능을 같이 해줌
        #with 정확히 어떤놈인지 정리해보기
        with open(pathList[i], "wb") as f:
            f.write(content)
            
        
    #urllib.error
    except HTTPError as e:
        print("HTTPError code : ", e.code)
    except URLError as e:
        print("URL Error code : ", e.code)
    else:
        print("---------")
        print("성공")
        print("---------")